import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [category, setCategory] = useState('Personal');

  useEffect(() => {
    axios.get(`http://localhost/api/questions.php?category=${category}`)
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des questions:', error);
      });
  }, [category]);

  return (
    <div>
      <h1>Liste des Questions pour l'Interview - {category}</h1>
      <select onChange={e => setCategory(e.target.value)} value={category}>
        <option value="Personal">Personnel</option>
        <option value="Experience">Expérience Professionnelle</option>
        <option value="Project">Projets en Cours</option>
        <option value="Skills">Compétences</option>
        <option value="Scenario">Scénario</option>
      </select>
      <ul>
        {questions.map((question, index) => (
          <li key={index}>{question.question}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
