import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [questions, setQuestions] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('Personal');

    useEffect(() => {
        axios.get(`http://localhost/api/questions.php`)
            .then(response => {
                setQuestions(response.data);
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des questions', error);
            });
    }, []);

    const filterQuestions = (category) => {
        return questions.filter(question => question.category === category);
    };

    return (
        <div className="App">
            <h1>Questions d'Entretien</h1>
            <div className="filter-options">
                <button onClick={() => setSelectedCategory('Personal')}>Personnel</button>
                <button onClick={() => setSelectedCategory('Experience')}>Expérience</button>
                <button onClick={() => setSelectedCategory('Project')}>Projet</button>
                <button onClick={() => setSelectedCategory('Skills')}>Compétences</button>
                <button onClick={() => setSelectedCategory('Scenario')}>Scénario</button>
            </div>
            <div className="questions-list">
                {filterQuestions(selectedCategory).map((question, index) => (
                    <div key={question.id} className="question">
                        <p><strong>{question.category}:</strong> {question.question}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
